            styles.layers[m].mode = {};
            for (var p in Style.defaults.mode) {
                styles.layers[m].mode[p] = Style.defaults.mode[p];
            }
        }
    }

    styles.camera = styles.camera || {}; // ensure camera object
    styles.lighting = styles.lighting || {}; // ensure lighting object

