transforms.globals:
vec3 hsv2rgb(vec3 c) { 
\
                                vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0); 
\
                                vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www); 
\
                                return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y); 
\
                            }

transforms.fragment:
vec3 c = vec3(v_world_position.z * .003 + u_time / 10., 1.0, 1.0); 
\
                            color = hsv2rgb(c);
